import { Component } from "react";
import { BitCodeWebContext } from "../appState/contextProvider";
import { TRADITIONAL_CHINESE } from "./language";

const traditional_chinese_label = React.lazy(() =>
  import("../label/traditional_chinese_label.json")
);

class ApplicationComponent extends Component {
  static contextType = BitCodeWebContext;

  get appState() {
    return this.context;
  }

  get label() {
    const { languageType } = this.appState.language;
    switch (languageType) {
      case TRADITIONAL_CHINESE:
        return;
      default:
        return;
    }
  }
}

export default ApplicationComponent;
