import React, { Component } from "react";
import { BitCodeWebContext } from "../appState/contextProvider";
import { TRADITIONAL_CHINESE } from "./language";

const english_label = React.lazy(() => import("../label/english_label.json"));
const traditional_chinese_label = React.lazy(() =>
  import("../label/traditional_chinese_label.json")
);

class ApplicationComponent extends Component {
  static contextType = BitCodeWebContext;
  _appStorage = new Storage();

  get appState() {
    return this.context;
  }

  get appStorage() {
    return this._appStorage;
  }

  get label() {
    const languageType = this.storage.getLanguage();
    console.log(languageType);
    switch (languageType) {
      case TRADITIONAL_CHINESE:
        return traditional_chinese_label;
      default:
        return traditional_chinese_label;
    }
  }
}

export default ApplicationComponent;
